$leftJoin = array(
    \'Data\' => array(
        \'class\' => \'msProductData\',
    ),
    \'Vendor\' => array(
        \'class\' => \'msVendor\',
        \'on\' => \'Data.vendor = Vendor.id\',
    ),
);

